import React, { useReducer, useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
import { Post, User } from '../api/types'
import { getUser } from '../api/user'

const PostItem = (props: Post) => {
  const formReducer = (state: Post | PostContent, event: FormData) => {
      return {
          ...state,
          [event.name]: event.value,
      }
  }

    const [author, setAuthor] = useState<Array<User>>([])
    const [formData, setFormData] = useReducer(
        formReducer,
        {} as User | UserProfile
    )

    type FormData = { name: string; value: string | number | undefined }



    let { id } = useParams() // post id from url

    async function _getUserId(id: number){
      const data = await getUser(id);
      convertToFormData(data);
    }

    useEffect(() => {
      // au start du composant
      _getUserId(Number(id));
    }, [id]);

    function convertToFormData(user: User): void {
        // helper to convert user data into formData
        // use it before set formData with API data
        // ex: convertToFormData(data):
        ;(Object.keys(user) as Array<keyof typeof user>).map((key) => {
            setFormData({
                name: key,
                value: user[key],
            })
        })
    }


    return (
        <Link to={`/post/${props.id}`} key={`key-${props.id}`}>
            <div className="card">
                <div className="card-image">
                    <figure className="image is-4by3">
                        <img
                            src={
                                props.postImageUrl ||
                                'https://picsum.photos/1280/960'
                            }
                            alt="Placeholder image"
                        />
                    </figure>
                </div>
                <div className="card-content">
                    <div className="media">
                        <div className="media-content">
                            <p className="title is-4 post-title">
                                {props.title}
                            </p>
                        </div>
                    </div>
                    <div className="content post-content">{props.body}</div>
                    {author && (
                      <footer className="card-footer">
                          <Link
                                to={`/users/${props.userId}`}
                                className="card-footer-item"
                          >
                                {author ? author.name : 'not defined'}
                          </Link>
                      </footer>
                    )}
                </div>
            </div>
        </Link>
    )
}

export default PostItem
