import React, { useReducer, useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
import { Post, User } from '../api/types'
import { getUser } from '../api/user'

const PostItem = (props: Post) => {
    const [author, setAuthor] = useState<Array<User>>([])

    let { id } = useParams() // post id from url

    const navigate = useNavigate() // create a navigate function instance

    async function _getUsers(id: number){
      const data = await getUser();
      setAuthor(data);
    }

    useEffect(() => {
      // au start du composant
      _getUsers(Number(id));
    }, [id]);




    return (
        <Link to={`/post/${props.id}`} key={`key-${props.id}`}>
            <div className="card">
                <div className="card-image">
                    <figure className="image is-4by3">
                        <img
                            src={
                                props.postImageUrl ||
                                'https://picsum.photos/1280/960'
                            }
                            alt="Placeholder image"
                        />
                    </figure>
                </div>
                <div className="card-content">
                    <div className="media">
                        <div className="media-content">
                            <p className="title is-4 post-title">
                                {props.title}
                            </p>
                        </div>
                    </div>
                    <div className="content post-content">{props.body}</div>
                    {author && (
                      <footer className="card-footer">
                          <Link
                                to={`/users/${props.userId}`}
                                className="card-footer-item"
                          >
                                {author ? author.name : 'not defined'}
                          </Link>
                      </footer>
                    )}
                </div>
            </div>
        </Link>
    )
}

export default PostItem
